mapper.select:
select
    id, price_id, formula, zone_num, country, starting_weight, ending_weight, package_type
from
price_computational_formula
where
    1=1

        and zone_num = #{key.zoneNum}


        and country = #{key.country}


        and starting_weight <= #{key.startingWeight}


        and ending_weight >= #{key.endingWeight}


        and package_type = #{key.packageType}


        and zone_num LIKE CONCAT( '%' , #{key.keyword} , '%' )
        or country LIKE CONCAT( '%' , #{key.keyword} , '%' )
        or package_type LIKE CONCAT( '%' , #{key.keyword} , '%' )
