mapper.select:
select main.id, main.start_date, main.end_date, main.currency, main.volume_divisor, main.account_type, main.main_status, main.special_commodity, sale.id as priceExpSaleId, sale.price_code, sale.price_name, sale.customer_groups_name, sale.customer_name, sale.channel_category, sale.price_status from price_exp_main main left join price_exp_sale sale on main.id = sale.price_main_id where 1=1
and main.volume_divisor = #{key.volumeDivisor}
and main.account_type = #{key.accountType}
and main.main_status = #{key.mainStatus}
and main.end_date - now() > '0 day' and main.end_date - now() <= '3 day'
and now() - main.end_date > '0 day'
and sale.customer_groups_id @> ${key.customerGroupsId}
and sale.customer_ids @> ${key.customerId}
and sale.price_status = #{key.priceStatus}
and sale.channel_category = #{key.channelCategory}
and sale.customer_name LIKE CONCAT( '%' , #{key.keyword} , '%' ) or sale.price_name LIKE CONCAT( '%' , #{key.keyword} , '%' ) or sale.customer_groups_name LIKE CONCAT( '%' , #{key.keyword} , '%' )

select main.id, main.start_date, main.end_date, main.currency, main.volume_divisor, main.account_type, main.main_status, main.special_commodity, cost.id as priceExpCostId, cost.price_status, cost.price_code, cost.price_name, cost.channel_category from price_exp_main main left join price_exp_cost cost on main.id = cost.price_main_id where 1=1
and main.volume_divisor = #{key.volumeDivisor}
and main.is_published_price = 1
and main.is_published_price = 2
and main.account_type = #{key.accountType}
and main.main_status = #{key.mainStatus}
and main.end_date - now() > '0 day' and main.end_date - now() <= '3 day'
and now() - main.end_date > '0 day'
and cost.partner_id = #{key.partnerId}
and cost.price_status = #{key.priceStatus}
and cost.channel_category = #{key.channelCategory}
and cost.price_name LIKE CONCAT( '%' , #{key.keyword} , '%' )
