mapper.select:
select id, start_date, end_date, currency, volume_divisor, account_type, special_commodity, price_code, price_sale_name as price_name, customer_groups_name, customer_name, channel_category, price_status, syn_status from lms_exp_price where inner_org_id = #{key.innerOrgId} AND is_published_price=2
and syn_status = #{key.synStatus}
and is_quote = #{key.isQuote}
and customer_groups_name != ''
and customer_name != ''
and volume_divisor = #{key.volumeDivisor}
and account_type = #{key.accountType}
and price_status = #{key.priceStatus}
and end_date > now()
and end_date - now() > '0 day' and end_date - now() <= '7 day'
and now() > end_date
and special_commodity @> '[${key.specialCommodity}]'::jsonb
and customer_groups_id @> '${key.customerGroupsId}'
and customer_ids @> '${key.customerId}'
and channel_category = #{key.channelCategory}
and ( price_sale_name LIKE CONCAT( '%' , #{key.keyword} , '%' ) or price_name LIKE CONCAT( '%' , #{key.keyword} , '%' ) )

select
    id ,start_date, end_date, currency, volume_divisor, account_type, special_commodity, price_status,
    price_code, price_name, channel_category, partner_id, partner_name, syn_status
from
lms_exp_price
where
inner_org_id = #{key.innerOrgId}
and is_published_price = 2
and partner_id>0

    and syn_status = #{key.synStatus}


    and is_quote = #{key.isQuote}


    and volume_divisor = #{key.volumeDivisor}


    and special_commodity @> '[${key.specialCommodity}]'::jsonb


    and account_type = #{key.accountType}


    and price_status = #{key.priceStatus}



    and end_date > now()



    and end_date - now() > '0 day' and end_date - now() <= '7 day'



    and now() > end_date
    and now() - end_date > '0 day'


    and partner_id = #{key.partnerId}


    and channel_category = #{key.channelCategory}


    and price_name LIKE CONCAT( '%' , #{key.keyword} , '%' )

select id, start_date, end_date, currency, account_type, price_status, price_code, price_sale_name, price_name, channel_category, special_commodity, volume_divisor from lms_exp_price where is_published_price = 1 and end_date>now()
and price_status = #{key.priceStatus}
and account_type = #{key.accountType}
and channel_category = #{key.channelCategory}
and price_name LIKE CONCAT( '%' , #{key.keyword} , '%' )
